import { LOGIN, ERROR, SUCCESS } from './actionType'
import axios from 'axios';


export const error = (email, password) => {
    return {
        type: ERROR,
        email,
        password
    }
}

export const success = (email, password) => {
    return {
        type: SUCCESS,
        email,
        password
    }
}

function fetchData(user) {
    axios.post('https://reqres.in/api/login/', user)
}

export default function login(user) {

    return fetchData(user)
        .then(res => {
            console.log(res);
            console.log(res.data);
            alert("Welcome!")
        })
        .catch(error => alert("The email address that you've entered doesn't match any account!"))


}
