import { fork, call, takeLatest, put } from 'redux-saga/effects'
import { errors, success, LOGIN } from '../actions/actionType'
// import { loginRequest } from '../api'
import axios from 'axios'

export function* loginRequestFlow(user) {
    try {
        const posts = yield call(axios
            .post('https://reqres.in/api/login/', user))
        yield put(success(user))
    } catch {
        yield put(errors(user))

    }
}

export function* loginRequestFinal() {
    yield call(LOGIN, loginRequestFlow);
}

export function* rootSaga() {
    yield fork(loginRequestFinal)
}
