import React, { useState } from 'react'
import { loginReducer } from './reducer/loginReducer';

function Form() {
    const [email, setEmail] = useState();
    const [pass, setPass] = useState();
    const onChangeEmail = (e) => setEmail(e.target.value)
    const onChangePass = (e) => setPass(e.target.value)

    const onSubmit = () => {
        dispatch(login(email, password))
    }
    return (
        <div>
            <input value={email} placeholder="Email" onChange={onChangeEmail} required />
            <input value={pass} placeholder="Password" onChange={onChangePass} required />
            <input className="button" type="submit" value={this.props.loading ? "Logging..." : "Login"} onClick={onSubmit} />

        </div>
    )
}

export default Form
