// import login from '../actions/loginAction'
// import axios from 'axios';
import { SUCCESS, ERROR, LOGIN } from '../actions/actionType'

const initialState = {
  user: {},
  loading: false,
  error: ''
}
export default function loginReducer(state = initialState, action) {
  switch (action.type) {
    case LOGIN:
      return {
        ...state,
        loading: true
      };
    case ERROR:
      return {
        ...state,
        loading: false,
        error: action.payload
      };
    case SUCCESS:

      return {
        ...state,
        loading: false,
        error: ''
      };
    default:
      return state;
  }
}
