import React, { useState } from 'react'
import { login } from './actions/loginAction';
import { useDispatch } from 'react-redux'

function Form() {
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const onChangeEmail = (e) => setEmail(e.target.value)
    const onChangePass = (e) => setPassword(e.target.value)
    const loading = false
    const dispatch = useDispatch()
    const onSubmit = () => {
        dispatch(login(email, password))
        loading = true
    }
    return (
        <div>
            <input value={email} placeholder="Email" onChange={onChangeEmail} required />
            <input value={password} placeholder="Password" onChange={onChangePass} required />
            <input className="button" type="submit" value={loading ? "Logging..." : "Login"} onClick={onSubmit} />

        </div>
    )
}

export default Form
