import { fork, call } from 'redux-saga/effects'
import { errors, success, LOGIN } from '../actions/actionType'
// import { loginRequest } from '../api'
import axios from 'axios'

export const createRequest = (user) => {
    return axios.post('https://reqres.in/api/login/', user)
}

export function* loginRequestFlow(user) {
    try {
        yield call(createRequest)
        yield call(success(user))
    } catch {
        yield call(errors(user))
    }
}


