import { LOGIN, ERROR, SUCCESS } from './actionType'
import axios from 'axios';


function error(user) {
    return {
        type: ERROR,
        user
    }
}

function success(user) {
    return {
        type: SUCCESS,
        user
    }
}

function fetchData(user) {
    axios.post('https://reqres.in/api/login/', user)
}

function login(user) {
    return fetchData(user)
        .then(res => {
            console.log(res);
            console.log(res.data);
            alert("Welcome!")
        })
        .catch(error => alert("The email address that you've entered doesn't match any account!"))
}
