import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import loginRequest from './actions/loginAction';

function Form() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const handleChangeEmail = (e) => {
    setEmail(e.target.value);
  }
  const handleChangePassword = (e) => {
    setPassword(e.target.value);
  }
  const user = {
    email: email,
    password: password
  };
  const dispatch = useDispatch();
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(user)
    dispatch(loginRequest(user))
  }

  return (
    <div>
      <form>
        <label>
          Person Email:
            <input type="text" email="email" value={email} onChange={handleChangeEmail} />
        </label>

        <label>
          Person Password:
            <input type="text" name="password" value={password} onChange={handleChangePassword} />
        </label>
        <button onClick={handleSubmit}>Login</button>
      </form>
      { !user.loading ? (<h2>Loading...</h2>)
        : user.error ? (alert(<h2>The email address that you've entered doesn't match any account!</h2>))
          : user.su ? (alert(<h1>Welcome!</h1>))
            : null
      }
    </div>
  )
}

export default Form;
