import { fork, call, takeLatest, takeEvery } from 'redux-saga/effects'
import { login, errors, success, LOGIN } from '../actions/actionType'
import { loginRequest } from '../api'

export function* loginRequestFlow(action) {
    yield takeLatest(loginRequest, action.payload)
    if (!action.payload.user) {
        yield call(errors)
    } else {
        yield call(success)
    }
}

export function* loginRequestFinal() {
    yield call(LOGIN, loginRequestFlow);
}

export function* rootSaga() {
    yield fork(loginRequestFinal)
}
