import { LOGIN } from '../actions/actionType'
import axios from 'axios';

const initialState = {
    login: {}
}
export default function loginReducer(state = initialState, action) {
    switch (action.type) {
        case LOGIN:
            axios.post('https://reqres.in/api/login/', { "email": email, "password": password })
                .then(res => {
                    console.log(res);
                    console.log(res.data);
                    alert("Welcome!")
                })
                .catch(error => alert("The email address that you've entered doesn't match any account!"))
            console.log("user2", email, password)
            // console.log("error.res", error.res)
            return {
                ...state
            }


        default:
            return state;
    }
}
