import { LOGIN } from '../actions/actionType'

const initialState = {
    user: []
}

export const loginReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            const isExist = state.user.find(el => el._id === action.user.email)
            // find email exist in array user
            const data = state.products.map(e => {
                if (e.password === action.user.password) {
                    return (
                        alert("Successful!")
                    )
                }
                // console.log("action.product.quantity", action.product.quantity)
                return e
            })
            // console.log("data", data)
            return {
                ...state,
                products: isExist ? data : alert("Failed!"),
            };
        default:
            return state;
    }
}
