import React from 'react';
import axios from 'axios';

export default class PersonList extends React.Component {
    state = {
        email: '',
        password: '',
    }

    handleChangeEmail = event => {
        this.setState({ email: event.target.value });
    }
    handleChangePassword = event => {
        this.setState({ password: event.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();

        const user = {
            email: this.state.email,
            password: this.state.password
        };
        console.log("user", user)

        axios.post('https://reqres.in/api/login/', user)
            .then(res => {
                console.log(res);
                console.log(res.data);
                alert("Welcome!")
            })

            .catch(error => alert("The email address that you've entered doesn't match any account!"))
        console.log("user2", user)
        // console.log("error.res", error.res)
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Person Email:
            <input type="text" email="email" onChange={this.handleChangeEmail} />
                    </label>

                    <label>
                        Person Password:
            <input type="text" name="password" onChange={this.handleChangePassword} />
                    </label>
                    <button type="submit">Login</button>
                </form>
            </div>
        )
    }
}
